import { Fragment, useEffect } from 'react';
import { useParams, Route, Link, useRouteMatch } from 'react-router-dom';
import Comments from '../components/comments/Comments'
import HighlightedQuote from '../components/quotes/HighlightedQuote';
import useHttp from '../hooks/use-http';
import { getSingleQuote } from '../lib/api';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const QuoteDetail = () => {
    const match = useRouteMatch()
    const params = useParams();

    const { sendRequest, status, error, data: loadedQuote } = useHttp(getSingleQuote, true)

    useEffect(() => {
        sendRequest(params.quoteId)
    }, [sendRequest, params])

    if (status === 'pending') {
        <div className='centered'>
            <LoadingSpinner />
        </div>
    }

    if (error) {
        return <p className='centered focused'>{error}</p>
    }

    // let quote = loadedData.find(q => q.id === params.quoteId)



    if (!loadedQuote) {
        return <p>No Quote Found</p>
    }

    console.log(loadedQuote.text)

    return (
        <Fragment>
            <HighlightedQuote text={loadedQuote.text} author={loadedQuote.author} />
            <Route path={match.path} exact>
                <div className='centered'>
                    <Link className='btn--flat' to={`${match.url}/comments`} >Comment </Link>
                </div>
            </Route>
            <Route path={`${match.path}/comments`} >
                <Comments />
            </Route>
        </Fragment>

    )
}
export default QuoteDetail;
