import { Fragment } from 'react';
import { useParams, Route, Link } from 'react-router-dom';
import Comments from '../components/comments/Comments'
import HighlightedQuote from '../components/quotes/HighlightedQuote';

const DUMMY_QUOTES = [
    { id: 'q1', author: 'Orkhan', text: 'Dummy quote 1' },
    { id: 'q2', author: 'Ali', text: 'Dummy quote 2' },
    { id: 'q3', author: 'Rasul', text: 'Dummy quote 3' },
    { id: 'q4', author: 'Hashim', text: 'Dummy quote 4' },
]

const QuoteDetail = () => {
    const params = useParams();
    let quote = DUMMY_QUOTES.find(q => q.id === params.quoteId)

    if (!quote) {
        return <p>No Quote Found</p>
    }

    return (
        <Fragment>
            <HighlightedQuote text={quote.text} author={quote.author} />
            <div className={classes.centered}>
                <Link to={`/quotes/${params.quoteId}/comments`} >Comment </Link>
            </div>
            <Route path={`/quotes/${params.quoteId}/comments`} >
                <Comments />
            </Route>
        </Fragment>

    )
}
export default QuoteDetail;
