import { Fragment, useEffect } from 'react'
import QuoteList from '../components/quotes/QuoteList'
import LoadingSpinner from '../components/UI/LoadingSpinner'
import useHttp from '../hooks/use-http'
import { getAllQuotes } from '../lib/api'
import NoQuotesFound from '../components/quotes/NoQuotesFound'

const DUMMY_QUOTES = [
    { id: 'q1', author: 'Orkhan', text: 'Dummy quote 1' },
    { id: 'q2', author: 'Ali', text: 'Dummy quote 2' },
    { id: 'q3', author: 'Rasul', text: 'Dummy quote 3' },
    { id: 'q4', author: 'Hashim', text: 'Dummy quote 4' },
]

const AllQuotes = () => {

    const { sendRequest, status, error, data: loadedData } = useHttp(getAllQuotes, true)


    useEffect(() => {
        sendRequest()

    }, [sendRequest])

    if (status === 'pending') {
        return (
            <div className='centered'>
                <LoadingSpinner />
            </div>
        )
    }

    if (error) {
        return <p className='centered focused'>{error}</p>
    }

    if (status === 'completed' && (!loadedData || loadedData.length===0)) {
        return (
            <NoQuotesFound/>
        )
    }



    return <QuoteList quotes={loadedData} />
}
export default AllQuotes;
